import React, { useRef, useState } from 'react';

const US_STATES = [
  'Alabama', 'Alaska', 'Arizona', 'Arkansas', 'California', 'Colorado', 'Connecticut', 'Delaware', 'Florida', 'Georgia',
  'Hawaii', 'Idaho', 'Illinois', 'Indiana', 'Iowa', 'Kansas', 'Kentucky', 'Louisiana', 'Maine', 'Maryland',
  'Massachusetts', 'Michigan', 'Minnesota', 'Mississippi', 'Missouri', 'Montana', 'Nebraska', 'Nevada', 'New Hampshire',
  'New Jersey', 'New Mexico', 'New York', 'North Carolina', 'North Dakota', 'Ohio', 'Oklahoma', 'Oregon', 'Pennsylvania',
  'Rhode Island', 'South Carolina', 'South Dakota', 'Tennessee', 'Texas', 'Utah', 'Vermont', 'Virginia', 'Washington',
  'West Virginia', 'Wisconsin', 'Wyoming'
];

function ProfileHeader() {
  const [editing, setEditing] = useState(false);
  const [name, setName] = useState('Abel Tesfaye');
  const [pronouns, setPronouns] = useState('he/him/his');
  const [location, setLocation] = useState('New York');
  const [profileImg, setProfileImg] = useState(process.env.PUBLIC_URL + '/weekend-headshot.jpg');
  const [teamsUrl, setTeamsUrl] = useState('');
  const [linkedinUrl, setLinkedinUrl] = useState('');
  const fileInputRef = useRef(null);

  // About section state (moved here)
  const [aboutEditing, setAboutEditing] = useState(false);
  const [about1, setAbout1] = useState(
    "Hi! My name is Jane, and I am a Product Marketing enthusiast with a product design background, which means I am passionate about full user journey. I love all things marketing including positioning, messaging, launch planning, and more."
  );
  const [about2, setAbout2] = useState(
    "I am a first-gen professional, and I highly value resilience and authenticity. I'm a great person to ask about career pivots, building confidence in marketing, or thriving in a fast-paced, high-impact role. Outside of work, I'm hiking when I can squeeze it in—I stay busy, but I am always happy to make time for meaningful connections. If you're curious and open to feedback, I would love to support your growth."
  );
  const [about1Draft, setAbout1Draft] = useState(about1);
  const [about2Draft, setAbout2Draft] = useState(about2);

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (event) => {
        setProfileImg(event.target.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleSave = () => {
    setEditing(false);
  };

  // About section handlers
  const handleAboutEdit = () => {
    setAbout1Draft(about1);
    setAbout2Draft(about2);
    setAboutEditing(true);
  };
  const handleAboutSave = () => {
    setAbout1(about1Draft);
    setAbout2(about2Draft);
    setAboutEditing(false);
  };
  const handleAboutCancel = () => {
    setAboutEditing(false);
  };

  return (
    <>
      <div className="profile-header">
        <div className="profile-image" style={{ position: 'relative' }}>
          <img src={profileImg} alt={name} />
          <div
            className="edit-badge"
            style={{ cursor: 'pointer' }}
            onClick={() => fileInputRef.current.click()}
            title="Edit profile picture"
          >
            ✏️
          </div>
          <input
            type="file"
            accept="image/*"
            ref={fileInputRef}
            style={{ display: 'none' }}
            onChange={handleImageChange}
          />
        </div>
        <div className="profile-info">
          {editing ? (
            <>
              <input
                type="text"
                value={name}
                onChange={e => setName(e.target.value)}
                style={{ fontSize: 36, fontWeight: 300, marginBottom: 5, color: '#2c3e50', width: '100%' }}
              />
              <input
                type="text"
                value={pronouns}
                onChange={e => setPronouns(e.target.value)}
                style={{ color: '#7f8c8d', fontSize: 14, marginBottom: 20, width: '100%' }}
              />
              <select
                value={location}
                onChange={e => setLocation(e.target.value)}
                style={{ color: '#7f8c8d', fontSize: 14, marginBottom: 20, width: '100%' }}
              >
                {US_STATES.map(state => (
                  <option key={state} value={state}>{state}</option>
                ))}
              </select>
              <input
                type="url"
                placeholder="Microsoft Teams link"
                value={teamsUrl}
                onChange={e => setTeamsUrl(e.target.value)}
                style={{ marginBottom: 8, width: '100%', fontSize: 14 }}
              />
              <input
                type="url"
                placeholder="LinkedIn profile link"
                value={linkedinUrl}
                onChange={e => setLinkedinUrl(e.target.value)}
                style={{ marginBottom: 8, width: '100%', fontSize: 14 }}
              />
              <button onClick={handleSave} style={{ marginTop: 8, padding: '6px 16px', borderRadius: 4, border: '1px solid #4a90e2', background: '#4a90e2', color: '#fff', fontWeight: 500, cursor: 'pointer' }}>Save</button>
            </>
          ) : (
            <>
              <h1 className="profile-name">{name}</h1>
              <div className="profile-pronouns">{pronouns}</div>
              <div className="profile-location">📍 {location}, NY</div>
            </>
          )}
          <div className="profile-details">
            <div className="detail-section">
              <h4>Role</h4>
              <p>Product Marketing Director</p>
              <div className="sub-text">since May 2018</div>
            </div>
            <div className="detail-section">
              <h4>Department</h4>
              <p>Global Marketing</p>
            </div>
            <div className="detail-section">
              <h4>Education</h4>
              <p>Monster's University</p>
            </div>
            <div className="detail-section">
              <h4>Mentees</h4>
              <p>2/3</p>
              <div className="sub-text">1 spot remaining</div>
            </div>
          </div>
        </div>
        <div className="social-icons">
          <a
            href={teamsUrl || '#'}
            className="social-icon"
            target="_blank"
            rel="noopener noreferrer"
            title="Microsoft Teams"
            style={{ background: 'transparent', padding: 0 }}
          >
            <img src={process.env.PUBLIC_URL + '/teams-logo.jpg'} alt="Teams" style={{ width: 32, height: 32, borderRadius: 4 }} />
          </a>
          <a
            href={linkedinUrl || '#'}
            className="social-icon linkedin"
            target="_blank"
            rel="noopener noreferrer"
            title="LinkedIn"
            style={{ background: 'transparent', padding: 0 }}
          >
            <img src={process.env.PUBLIC_URL + '/linkedin-logo.png'} alt="LinkedIn" style={{ width: 32, height: 32, borderRadius: 4 }} />
          </a>
          <div
            className="edit-icon"
            style={{ cursor: 'pointer' }}
            onClick={() => setEditing(true)}
            title="Edit profile section"
          >
            ✏️
          </div>
        </div>
      </div>
      {/* About section right after header */}
      <div className="card" style={{ marginTop: 24 }}>
        <div className="card-header">
          <h3 className="card-title">About</h3>
          <span
            className="edit-icon"
            style={{ cursor: 'pointer' }}
            onClick={handleAboutEdit}
            title="Edit about section"
          >
            ✏️
          </span>
        </div>
        <div className="about-section">
          {aboutEditing ? (
            <>
              <textarea
                value={about1Draft}
                onChange={e => setAbout1Draft(e.target.value)}
                rows={3}
                style={{ width: '100%', marginBottom: 10, fontSize: 16 }}
              />
              <textarea
                value={about2Draft}
                onChange={e => setAbout2Draft(e.target.value)}
                rows={3}
                style={{ width: '100%', marginBottom: 10, fontSize: 16 }}
              />
              <button
                onClick={handleAboutSave}
                style={{ padding: '6px 16px', borderRadius: 4, border: '1px solid #4a90e2', background: '#4a90e2', color: '#fff', fontWeight: 500, cursor: 'pointer', marginRight: 8 }}
              >
                Save
              </button>
              <button
                onClick={handleAboutCancel}
                style={{ padding: '6px 16px', borderRadius: 4, border: '1px solid #aaa', background: '#fff', color: '#333', fontWeight: 500, cursor: 'pointer' }}
              >
                Cancel
              </button>
            </>
          ) : (
            <>
              <p className="about-text">{about1}</p>
              <p className="about-text">{about2}</p>
            </>
          )}
        </div>
      </div>
    </>
  );
}

export default ProfileHeader;
